export const roleConverter = role =>
	role === 'admin'
		? '超級管理員'
		: role === 'staff'
		? '管理員'
		: role === 'user'
		? '會員'
		: role === 'trial'
		? '試用會員'
		: '訪客';

export const photoURLPathConverter = photoURL => {
	if (!photoURL) {
		return `/assets/images/avatar/bull.svg`;
	}
	if (photoURL.startsWith('assets/images/avatars') || photoURL.startsWith('http')) {
		return photoURL;
	}
	return `${process.env.API_URL}/uploads/avatar/${photoURL}`;
};

export const avatarNameToPathConverter = imageName => {
	if (!imageName) {
		return 'assets/images/avatars/penguin.png';
	}
	if (imageName.startsWith('assets/images/avatars') || imageName.startsWith('http')) {
		return imageName;
	}
	return `${process.env.API_URL}/uploads/avatar/${imageName}`;
};

export const socialLogoConverter = provider => {
	switch (provider.toLowerCase()) {
		case 'facebook':
			// return 'data:image/svg+xml;base64,';
			return 'data:image/svg+xml;base64,';
		case 'instgram':
			return 'data:image/svg+xml;base64,';
		case 'youtube':
			return 'data:image/svg+xml;base64,';
		case 'line':
			return 'data:image/svg+xml;base64,';
		case 'email':
			return 'data:image/svg+xml;base64,';

		case 'google':
		default:
			return 'data:image/svg+xml;base64,';
	}
};
